import { Editor, Range, Path, NodeEntry, Transforms } from 'slate'
import {ListNode, ListItemNode} from './types'

export const onKeyDown = () => (
    e: KeyboardEvent,
    editor: Editor
) => {
    let {selection} = editor
    if (e.key === 'Tab' && selection) {
        e.preventDefault()
        const { anchor } = selection
        const block = Editor.above(editor, {
            match: n => Editor.isBlock(editor, n),
        })
        if (block && block[0].type == 'list-item') {
            console.log("tab in list-item")
            if (Range.isCollapsed(selection)) {
                const path = block[1]
                const start = Editor.start(editor, path)
                const range = {anchor, focus: start}
                let beforeText = Editor.string(editor, range)
                if (beforeText.length){
                    return
                }
            }
            // move down with tab
            const tab = !e.shiftKey;
            if (tab) {
                moveListItemDown(editor, block[1]);
            }
        }
    }
};

function moveListItemDown(editor:Editor, path: Path){
    const entry = Editor.parent(editor, path)
    let node: ListNode = entry[0] as ListNode
    if (node.type.endsWith( '-list')) {
        console.log("indent:",node.indent)
        node.indent ++
        Transforms.splitNodes(editor, {at: path})
    }
}
